mapper.update:
UPDATE choice
SET
choose_date = #{chooseDate}
WHERE
stu_id = #{stuId}
AND cou_name = #{couName}

UPDATE choice
SET
  withdraw_date = #{withdrawDate}
WHERE
  stu_id = #{stuId}
  AND cou_name = #{couName}

UPDATE choice


    cou_name = #{couName},


    choose_date = #{chooseDate},


    withdraw_date = #{withdrawDate}



  stu_id = #{stuId}
  AND del_flag != 0

UPDATE choice
SET
  del_flag = 0

  stu_id = #{stuId}
  AND cou_name = #{couName}
  AND choose_date = #{chooseDate}
